import React from 'react';
import { Container, Grid, makeStyles, Typography  } from '@material-ui/core';
const useStyles = makeStyles({
    root: {
        margin: 0,
        color: 'rgb(45, 55, 72)',
        fontFamily: 'Inter, sans-serif',
        fontWeight: 400,
        fontSize: '1rem',
        lineHeight: 1.5,
        backgroundColor: 'rgb(255, 255, 255)',
    },
    page: {
        marginTop: '40px',
        boxShadow: 'rgb(140 152 164 / 18%) 0px 10px 40px 10px',
        borderRadius: '8px',
    },
    head:{
        backgroundColor: 'rgb(25, 118, 210)',
        borderRadius: '8px',  
        textAlign: 'center',
    },
    headInner: {
        fontFamily: 'Inter, sans-serif',
        padding: '48px 32px',
        color: 'rgb(255, 255, 255)',
        fontWeight: 700,
    },
    headArc:{
        width: '100%',
        marginBottom: '-8px',
        transition: 'box-shadow 300ms cubic-bezier(0.4, 0, 0.2, 1) 0ms',
    },
    title:{
        fontSize: '1.25rem',
    },
    test: {
        backgroundColor: 'red',
    }
})

export default function Conditions() {
    const classes = useStyles();
    return (
        <div className={classes.root}>
           <Container>
            <div className={classes.page}>
                <div className={classes.head}>
                    <Typography className={classes.headInner} variant="h3">Chính sách, điều khoản.</Typography>
                    <svg className={classes.headArc} preserveAspectRatio="none" 
                    xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" viewBox="0 0 1920 100.1">
                    <path fill="#fff" d="M0,0c0,0,934.4,93.4,1920,0v100.1H0L0,0z"></path></svg>
                </div>
                <Grid className={classes.test} xs={12} sm={9}>
                    <Typography variant='h3'>1. Miễn phí</Typography>
                    <p>Team 2 là website cung cấp thông tin học tiếng Nhật hoàn toàn miễn phí.</p>

                </Grid>
                
            </div>
           </Container>
        </div>
    )
}
