import React from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/styles';
import Button from '@material-ui/core/Button';
import ArrowRightAltIcon from '@material-ui/icons/ArrowRightAlt';
import ImageList from '@material-ui/core/ImageList';
import ImageListItem from '@material-ui/core/ImageListItem';
import itemData from './itemData.js';
import OurTeamCards from '../../components/cards/ourteamCards'


const useStyles = makeStyles((theme) => ({
    root:{
        flexWrap: 'wrap',
        justifyContent: 'center',
        maxWidth: '100%',
        marginTop: '55px',
        textAlign: 'center',
        lineHeight: '1.6',
        fontFamily: 'Inter',
        fontWeight: '500',
        color: 'rgb(100, 110, 115)',
    },
    appendix:{
        margin: '0px 0px 0.35em',
        fontSize: '1rem',
        lineHeight: '1.5',
        color: 'rgb(100, 110, 115)',
        textTransform: 'uppercase',
        fontWeight: '500',
    },
    solgan:{
        margin:'0px 0px 10px 0px ',
        lineHeight: '1.2',
        fontWeight: '700',
        color: 'rgb(45, 55, 72)',
    },
    btn:{
        marginTop: '40px',
        textTransform: 'capitalize',
    },
    icon:{
        marginLeft: '5px'
    },
    imageList: {
        width: 500,
        height: 450,
    },
    ourteam: {
        backgroundColor: 'rgb(240, 242, 245)', 
        padding: '10px',
    }
}))

export default function About() {
    const classes = useStyles();
    return (
        <Container>
            <div className={classes.root}>
                <div>
                    <Typography className={classes.appendix} variant='p'>
                        ABOUT US
                    </Typography>
                </div>
                <div>
                    <Typography className={classes.solgan} variant='h3' component='h5' >
                        Chúng tôi đem lại cho cộng đồng IT sử dụng tiếng Nhật những chia sẽ hữu ích.
                    </Typography>
                </div>   
                <div>
                    <Typography variant='h6'>
                        Với một đội ngũ dày dặn kinh nghiệm vê IT, tiếng Nhật;
                        kết hợp với một cộng đồng lớn những người đang học tập cũng như đang sữ dụng tiếng Nhật trong lĩnh vực IT.
                        Chúng tôi hy vọng sẽ mang lại những đóng góp, trợ giúp, những chia sẽ hữu ích cho bạn.
                    </Typography>
                </div>   
                <Button className={classes.btn} variant="contained" color="primary" textTransform="lower" >
                    Contacts us   
                    <ArrowRightAltIcon className={classes.icon} />
                </Button>
            </div>
            <div className={classes.root}>
                <ImageList rowHeight={260} cols={4}>
                    {itemData.map((item) => (
                    <ImageListItem key={item.img} cols={item.cols || 1} rows={item.rows || 1}>
                        <img src={item.img} alt={item.title} />
                    </ImageListItem>
                    ))}
                </ImageList>
            </div>  
            <div className={classes.root} container item xs={12} sm={3} spacing={1}>
                <div>
                    <Typography className={classes.appendix} variant='p'>
                        our team
                    </Typography>
                </div>
                <div>
                    <Typography className={classes.solgan} variant='h3' >
                        Đội ngũ xây dựng Team2 IT.
                    </Typography>
                </div>
            </div>
            <div className={classes.root,classes.ourteam}>
                <OurTeamCards/>
            </div>
        </Container>
    )
}
