import React from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/styles';

const useStyles = makeStyles((theme) => ({
    root:{
        justifyContent: 'center',
        maxWidth: '100%',
        marginTop: '65px',
        fontFamily: 'Inter',
        
    },
    appendix:{
        margin: '0px 0px 0.35em',
        fontSize: '1rem',
        lineHeight: 1.5,
        textAlign: 'center',
        color: 'rgb(100, 110, 115)',
        textTransform: 'uppercase',
        fontWeight: 500,
    }
}))

export default function About() {
    const classes = useStyles();
    return (
        <Container>
            <Grid className={classes.root} container item xs={12} sm={3} spacing={1}>
                <Grid>
                    <Typography className={classes.appendix} variant='p'>
                        ABOUT US
                    </Typography>
                </Grid>
                <Grid>
                    <Typography className={classes.appendix} variant='h2'>
                        Chúng tôi đem lại cho cộng đồng IT sử dụng tiếng Nhật những chia sẽ hữu ích.
                    </Typography>
                </Grid>    
            </Grid>
        </Container>
    )
}
