import React from 'react';
import Container from '@material-ui/core/Container';
import Typography from '@material-ui/core/Typography';
import { Grid, makeStyles} from '@material-ui/core';
import Button from '@material-ui/core/Button';
import ArrowRightAltIcon from '@material-ui/icons/ArrowRightAlt';
import ImageList from '@material-ui/core/ImageList';
import ImageListItem from '@material-ui/core/ImageListItem';
import itemData from './itemData.js';
import OurTeamCards from '../../components/cards/ourteamCards'
import Box from '@material-ui/core/Box';
import { Link } from 'react-router-dom';


const useStyles = makeStyles((theme) => ({
    root:{
        flexWrap: 'wrap',
        justifyContent: 'center',
        maxWidth: '100%',
        marginTop: '55px',
        textAlign: 'center',
        lineHeight: '1.6',
        fontFamily: 'Inter',
        fontWeight: '500',
        // color: 'rgb(100, 110, 115)',
    },
    appendix:{
        margin: '0px 0px 0.35em',
        fontSize: '1rem',
        lineHeight: '1.5',
        color: 'rgb(100, 110, 115)',
        textTransform: 'uppercase',
        fontWeight: '500',
    },
    solgan:{
        margin:'0px 0px 10px 0px ',
        lineHeight: '1.2',
        fontWeight: '700',
        color: 'rgb(45, 55, 72)',
    },
    btn:{
        marginTop: '20px',
    },
    icon:{
        marginLeft: '5px'
    },
    imageList: {
        width: 500,
        height: 450,
    },
    ourteam: {
        backgroundColor: 'rgb(240, 242, 245)', 
        padding: '10px',
        marginTop: '40px'
    },
}))

export default function About() {
    const classes = useStyles();
    return (
        <Container>
            <Grid container>
                <Grid item xs={12} sm={6} className={classes.root}>
                    <div>
                        <p className={classes.appendix}>
                            ABOUT US
                        </p>
                    </div>
                    <Box className={classes.solgan} fontSize={{ xs: 'h6.fontSize', sm: 'h4.fontSize', md: 'h3.fontSize' }}>
                        Chúng tôi đem lại cho cộng đồng IT sử dụng tiếng Nhật những chia sẽ hữu ích.
                    </Box>
                    <Box fontSize={{ xs: 'body2.fontSize', sm: 'h5.fontSize' }}>
                        Với một đội ngũ có kinh nghiệm về IT và tiếng Nhật;
                        kết hợp với một cộng đồng lớn những người đang học tập, làm việc đang sữ dụng tiếng Nhật trong lĩnh vực IT.
                        Chúng tôi hy vọng sẽ mang lại những đóng góp, trợ giúp, những chia sẽ hữu ích cho bạn.
                    </Box>
                </Grid><Grid item xs={12} sm={6} className={classes.root}>
                    <div>
                        <p className={classes.appendix}>
                            ABOUT US
                        </p>
                    </div>
                    <Box className={classes.solgan} fontSize={{ xs: 'h6.fontSize', sm: 'h4.fontSize', md: 'h3.fontSize' }}>
                        Chúng tôi đem lại cho cộng đồng IT sử dụng tiếng Nhật những chia sẽ hữu ích.
                    </Box>
                    <Box fontSize={{ xs: 'body2.fontSize', sm: 'h5.fontSize' }}>
                        Với một đội ngũ có kinh nghiệm về IT và tiếng Nhật;
                        kết hợp với một cộng đồng lớn những người đang học tập, làm việc đang sữ dụng tiếng Nhật trong lĩnh vực IT.
                        Chúng tôi hy vọng sẽ mang lại những đóng góp, trợ giúp, những chia sẽ hữu ích cho bạn.
                    </Box>
                </Grid>
            </Grid>
            <div className={classes.root}>
                <ImageList rowHeight={260} cols={4}>
                    {itemData.map((item) => (
                    <ImageListItem key={item.img} cols={item.cols || 1} rows={item.rows || 1}>
                        <img src={item.img} alt={item.title} />
                    </ImageListItem>
                    ))}
                </ImageList>
            </div>  
            
        </Container>
    )
}
