import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Avatar from '@material-ui/core/Avatar';
import GitHubIcon from '@material-ui/icons/GitHub';
import FacebookIcon from '@material-ui/icons/Facebook';

const useStyles = makeStyles({

  root: {
    maxWidth: 345,
    minHeight: 320,
  },
  team: {
    textAlign: 'left',
    fontSize: 18,
  },
  teamAV:{
    height: '80px',
    width: '80px',
    marginBottom: '10px',
  },
  speak:{
    marginTop: '20px',
  },
  contact:{
    position: 'relative',
    bottom: 0,
    maxHeight: '25px',
  }, 
  icon:{
    width: '20px',
    height: '20px'
  }
});

export default function OneCard({ infor}) {
  const classes = useStyles();

  return (
    <Card className={classes.root}>
      <CardActionArea>
        <CardContent className={classes.team}>
          <Avatar className={classes.teamAV} alt={infor.name} src={infor.avatar} />
          <Typography variant="h6" component="h5">
           {infor.name}
          </Typography>
          <Typography variant="body2" color="rgb(100, 110, 115)" component="p">
            {infor.jobTitle}
          </Typography>
          <Typography className={classes.speak} variant="span" color="rgb(100, 110, 115)" component="p">
            {infor.spoken}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions className={classes.contact}>
        <Button size="small" color="primary">
          <GitHubIcon className={classes.icon}/>
        </Button>
        <Button size="small" color="primary">
          <FacebookIcon/>
        </Button>
      </CardActions>
    </Card>
  )
}
