import React from 'react';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/styles';

const useStyles = makeStyles((theme) => ({
    root:{
        flexWrap: 'wrap',
        justifyContent: 'center',
        maxWidth: '100%',
        marginTop: '65px',
        textAlign: 'center',
        lineHeight: '1.6',
        fontFamily: 'Inter',
        fontWeight: '500',
        color: 'rgb(100, 110, 115)',
    },
    appendix:{
        margin: '0px 0px 0.35em',
        fontSize: '1rem',
        lineHeight: '1.5',
        color: 'rgb(100, 110, 115)',
        textTransform: 'uppercase',
        fontWeight: '500',
    },
    solgan:{
        margin:'0px',
        lineHeight: '1.2',
        fontWeight: '700',
        color: 'rgb(45, 55, 72)',
    },
}))

export default function About() {
    const classes = useStyles();
    return (
        <Container>
            <Grid className={classes.root} container item xs={12} sm={3} spacing={1}>
                <Grid>
                    <Typography className={classes.appendix} variant='p'>
                        ABOUT US
                    </Typography>
                </Grid>
                <Grid>
                    <Typography className={classes.solgan} variant='h3'>
                        Chúng tôi đem lại cho cộng đồng IT sử dụng tiếng Nhật những chia sẽ hữu ích.
                    </Typography>
                </Grid>   
                <Grid>
                    <Typography variant='h5 '>
                        Với một đội ngũ dày dặn kinh nghiệm vê IT, tiếng Nhật;
                        kết hợp với một cộng đồng lớn những người đang học tập cũng như đang sữ dụng tiếng Nhật trong lĩnh vực IT.
                        Chúng tôi hy vọng sẽ mang lại những đóng góp, trợ giúp, những chia sẽ hữu ích cho bạn.
                    </Typography>
                </Grid>   
            </Grid>
        </Container>
    )
}
