import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Avatar from '@material-ui/core/Avatar';

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
  },
  team: {
    textAlign: 'left',
  },
  teamAV:{
    height: '80px',
    width: '80px',
  }
});

export default function OneCard({ infor}) {
  const classes = useStyles();

  return (
    
    <Card className={classes.team}>
      <CardActionArea>
        <CardContent>
          <Avatar className={classes.team} alt={infor.name} src={infor.avatar} />
          <Typography variant="h5" component="h2">
           {infor.name}
          </Typography>
          <Typography variant="body2" color="rgb(100, 110, 115)" component="p">
            {infor.jobTitle}
          </Typography>
          <Typography variant="h6" color="rgb(100, 110, 115)" component="p">
            {infor.spoken}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" color="primary">
          Share
        </Button>
        <Button size="small" color="primary">
          Learn More
        </Button>
      </CardActions>
    </Card>
  )
}
